"""
    NameToRGB(ColorName)
    Returns the standard 256 RGB for a given color name or color code.
    E.g. NameToRGB("black"):
        returns [0,0,0]

        NameToRGB("white"):
            returns [255,255,255]
"""
def NameToRGB(ColorName="black"):
    Standard_RGB=[0,0,0]
    if ColorName =="":
        return [0, 0, 0] #black
    if ColorName[0] == "#":
        # Standard 6 digits Hex code
        if len(ColorName)==7:
            HexR = ColorName[1:3]
            HexG = ColorName[3:5]
            HexB = ColorName[5:7]
            try:
                Standard_RGB[0] = int(HexR, 16)
                Standard_RGB[1] = int(HexG, 16)
                Standard_RGB[2] = int(HexB, 16)
            except:
                PrintError("Can not Parse the Color Hex code !\n"+
                    "Change it to Black.")
                Standard_RGB=[0,0,0]
        # Not so standard 3 digits Hex code
        elif len(ColorName)==4:
            HexR = ColorName[1]+ ColorName[1]
            HexG = ColorName[2]+ ColorName[2]
            HexB = ColorName[3]+ ColorName[3]
            try:
                Standard_RGB[0] = int(HexR, 16)
                Standard_RGB[1] = int(HexG, 16)
                Standard_RGB[2] = int(HexB, 16)
            except:
                PrintError ("Can not Parse the Color Hex code !\n"+
                    "Change it to Black.")
                Standard_RGB=[0,0,0]

        else:
            PrintError ("Invalid Color")
            Standard_RGB=[0,0,0]

    else:
        #Matlab or Python like Color Name defination
        if (ColorName == "black") | (ColorName == "k") |  (ColorName == "blk") :
            Standard_RGB = [0, 0, 0]
        elif (ColorName == "red") | (ColorName == "r") :
            Standard_RGB = [255, 0, 0,]
        elif (ColorName == "green") |(ColorName == "g") :
            Standard_RGB = [0, 255, 0,]
        elif (ColorName == "blue")|(ColorName == "b"):
            Standard_RGB = [0, 0, 255,]
        elif (ColorName == "yellow")|(ColorName == "y"):
            Standard_RGB = [255, 255, 0,]
        elif (ColorName == "magenta")|(ColorName == "m"):
            Standard_RGB = [255, 0, 255,]
        elif (ColorName == "cyan")|(ColorName == "c"):
            Standard_RGB = [0, 255, 255,]
        elif (ColorName == "white")|(ColorName == "w"):
            Standard_RGB = [255, 255, 255,]
        else:
            try:
                Standard_RGB = X11color[ColorName]
            except:
                print("Can not get your Color name!!\nChange it to Black")
                Standard_RGB = X11color["black"]

    return Standard_RGB
