from os import path
from os.path import join, abspath, dirname
from setuptools import setup, find_packages

here = path.abspath(path.dirname(__file__))

with open(path.join(here, 'README.md')) as f:
    readme = f.read()

setup(
    name="v2d",
    version="0.0.1",
    packages=find_packages(),
    long_description=readme,
    description="v2d - cli generates new variations of an input based on similarity matrix generated by v2d-similarity.",
    author="José Ignacion Escribano & Miguel Hernández & Alfonso Muñoz",
    author_email="douncoge@gmail.com",
    url="https://github.com/PantherLab/v2d-cli",
    license="MIT",
    python_requires='>3.5.0',
    install_requires=[
        'tqdm',
        'colorama',
        'python-whois',
        'requests'
    ],
    extras_require={
        'dev': [
            'flake8'
        ]
    },
    entry_points={
        'console_scripts': [
            'v2d = v2d.main:main',
        ]
    },
    classifiers=[
        'Environment :: Console',
        'Intended Audience :: System Administrators',
        'Intended Audience :: Other Audience',
        'License :: OSI Approved :: BSD License',
        'Operating System :: MacOS',
        'Operating System :: Microsoft :: Windows',
        'Operating System :: POSIX',
        'Programming Language :: Python :: 3',
    ],
    )
